import { Component } from 'react'
import { func, string } from 'prop-types';
import { generateOptions } from '../../helpers/itemGenerators';
import { PRIORITIES, STATUSES } from '../../constants/itemConstants';

export default class ItemCard extends Component {

  state = {
    titleEditMode: false,
    descriptionEditMode: false,
    priorityEditMode: false,
    statusEditMode: false,
  };

  handleDeleteClick = () => {
    console.log('handle click');
  }

  handleEditModeOn = (event) => {
    console.log(event.target);
    this.setState({
      [`${event.target.getAttribute('name')}EditMode`]: true,
    });
  }

  handleBlur = (event) => {
    this.setState({
      [`${event.target.getAttribute('name')}EditMode`]: false,
    });
  }

  handleChange = (event) => {
    this.props.onUpdate(this.props.id, { [event.target.name]: event.target.value });
  }

  render() {
    const {
      titleEditMode,
      descriptionEditMode,
      priorityEditMode,
      statusEditMode,
    } = this.state;

    const {
      title,
      description,
      status,
      priority,
    } = this.props;

    return (
      <div className="ItemCard">
        {!this.state.titleEditMode && <h2 onClick={this.handleEditModeOn} name="title">{title}</h2>}
        {titleEditMode && (
          <h2>
            <input
              type="text"
              value={title}
              name="title"
              onBlur={this.handleBlur}
              onChange={this.handleChange}
            />
          </h2>
        )}
        <hr className='line'></hr>
        <h3>Description:</h3>
        {!descriptionEditMode && <p className='description' onClick={this.handleEditModeOn} name="description">{description}</p>}
        {descriptionEditMode && (
          <p className='description'> 
            <textarea
              onBlur={this.handleBlur}
              value={description}
              name="description"
              onChange={this.handleChange}
            />
          </p>
        )}
        <h3>Priority:</h3>
        {!priorityEditMode && <p className='description' onClick={this.handleEditModeOn} name="priority">{priority}</p>}
        {priorityEditMode && (
          <p>
            {generateOptions('priority', priority, PRIORITIES)}
          </p>
        )}
        <h3>Status:</h3>
        {!statusEditMode && <p className='description' onClick={this.handleEditModeOn} name="status">{status}</p>}
        {statusEditMode && (
          <p>
            {generateOptions('status', status, STATUSES)}
          </p>
        )}
        <button onClick={this.handleDeleteClick}>
        <svg id="Layer_1" data-name="Layer 1" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 105.16 122.88"><defs></defs><title>delete</title><path class="cls-1" d="M11.17,37.16H94.65a8.4,8.4,0,0,1,2,.16,5.93,5.93,0,0,1,2.88,1.56,5.43,5.43,0,0,1,1.64,3.34,7.65,7.65,0,0,1-.06,1.44L94,117.31v0l0,.13,0,.28v0a7.06,7.06,0,0,1-.2.9v0l0,.06v0a5.89,5.89,0,0,1-5.47,4.07H17.32a6.17,6.17,0,0,1-1.25-.19,6.17,6.17,0,0,1-1.16-.48h0a6.18,6.18,0,0,1-3.08-4.88l-7-73.49a7.69,7.69,0,0,1-.06-1.66,5.37,5.37,0,0,1,1.63-3.29,6,6,0,0,1,3-1.58,8.94,8.94,0,0,1,1.79-.13ZM5.65,8.8H37.12V6h0a2.44,2.44,0,0,1,0-.27,6,6,0,0,1,1.76-4h0A6,6,0,0,1,43.09,0H62.46l.3,0a6,6,0,0,1,5.7,6V6h0V8.8h32l.39,0a4.7,4.7,0,0,1,4.31,4.43c0,.18,0,.32,0,.5v9.86a2.59,2.59,0,0,1-2.59,2.59H2.59A2.59,2.59,0,0,1,0,23.62V13.53H0a1.56,1.56,0,0,1,0-.31v0A4.72,4.72,0,0,1,3.88,8.88,10.4,10.4,0,0,1,5.65,8.8Zm42.1,52.7a4.77,4.77,0,0,1,9.49,0v37a4.77,4.77,0,0,1-9.49,0v-37Zm23.73-.2a4.58,4.58,0,0,1,5-4.06,4.47,4.47,0,0,1,4.51,4.46l-2,37a4.57,4.57,0,0,1-5,4.06,4.47,4.47,0,0,1-4.51-4.46l2-37ZM25,61.7a4.46,4.46,0,0,1,4.5-4.46,4.58,4.58,0,0,1,5,4.06l2,37a4.47,4.47,0,0,1-4.51,4.46,4.57,4.57,0,0,1-5-4.06l-2-37Z"/></svg>
        </button>
        {/* button delete */}
      </div>
    );
  }
}

ItemCard.propTypes = {
  id: string,
  title: string,
  description: string,
  priority: string,
  status: string,
  onUpdate: func,
};

ItemCard.defaultProps = {
  title: 'Hello world',
  description: 'Lorem ipsum blablabla Lorem ipsum blablabla Lorem ipsum blablabla Lorem ipsum blablabla',
  status: 'TODO',
  priority: 'HIGH',
};
