import { Component } from 'react'
import { func, string } from 'prop-types';
import { generateOptions } from '../../helpers/itemGenerators';
import { PRIORITIES, STATUSES } from '../../constants/itemConstants';

export default class ItemCard extends Component {

  state = {
    titleEditMode: false,
    descriptionEditMode: false,
    priorityEditMode: false,
    statusEditMode: false,
    isOnDelete: false,
  };

  handleDeleteClick = () => {
    this.setState({ isOnDelete: true });
  }

  handleEditModeOn = (event) => {
    console.log(event.target);
    this.setState({
      [`${event.target.getAttribute('name')}EditMode`]: true,
    });
  }

  handleBlur = (event) => {
    this.setState({
      [`${event.target.getAttribute('name')}EditMode`]: false,
    });
  }

  handleChange = (event) => {
    this.props.onUpdate(this.props.id, { [event.target.name]: event.target.value });
  }

  render() {
    const {
      titleEditMode,
      descriptionEditMode,
      priorityEditMode,
      statusEditMode,
    } = this.state;

    const {
      title,
      description,
      status,
      priority,
    } = this.props;

    return (
      !this.state.isOnDelete && <div className="ItemCard">
        {!this.state.titleEditMode && <p onClick={this.handleEditModeOn} name="title">{title}</p>}
        {titleEditMode && (
          <p>
            <input
              type="text"
              value={title}
              name="title"
              onBlur={this.handleBlur}
              onChange={this.handleChange}
            />
          </p>
        )}

        {!descriptionEditMode && <p onClick={this.handleEditModeOn} name="description">{description}</p>}
        {descriptionEditMode && (
          <p>
            <textarea
              onBlur={this.handleBlur}
              value={description}
              name="description"
              onChange={this.handleChange}
            />
          </p>
        )}

        {!priorityEditMode && <p onClick={this.handleEditModeOn} name="priority">{priority}</p>}
        {priorityEditMode && (
          <p
            defaultValue={priority}
            onBlur={this.handleBlur}
            name="priority"
            onChange={this.handleChange}>
            {generateOptions('priority', priority, PRIORITIES, this.handlePriorityClick)}
          </p>
        )}

        {!statusEditMode && <p onClick={this.handleEditModeOn} name="status">{status}</p>}
        {statusEditMode && (
          <p
            defaultValue={status}
            name="status"
            onBlur={this.handleBlur}
            onChange={this.handleChange}>
            {generateOptions('status', status, STATUSES, this.handleStatusClick)}
          </p>
        )}
        <button value={this.state.isOnDelete} onClick={this.handleDeleteClick}>Delete</button>
        {/* button delete */}
      </div>
    );
  }
}

ItemCard.propTypes = {
  id: (string,Number),
  title: string,
  description: string,
  priority: string,
  status: string,
  onUpdate: func,
};

ItemCard.defaultProps = {
  title: 'Hello world',
  description: 'Lorem ipsum blablabla Lorem ipsum blablabla Lorem ipsum blablabla Lorem ipsum blablabla',
  status: 'TODO',
  priority: 'HIGH',
};
